package com.biz.var;

public class Variable01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * 숫자 3개를 변수에 저장하고
		 * 변수에 저장된 숫자 3개를 덧셈, 곱셈을 계산
		 */
		System.out.println(30+40+50);
		System.out.println(30*40*50);
		
		int intNum1 = 30;
		int intNum2 = 40;
		int intNum3 = 50;
		System.out.println(intNum1 + intNum2 + intNum3);
		System.out.println(intNum1 * intNum2 * intNum3);
		
		intNum1 = 26;
		intNum2 = 44;
		intNum3 = 48;
		
		int intSum = intNum1 + intNum2 + intNum3;
		System.out.println(intSum);
		int intMulti = intNum1 * intNum2 * intNum3;
		System.out.println(intMulti);

	}

}




